   118→
   119→  // dropdown icons state
   120→  const [dropdownIcons, setDropdownIcon] = useState(initDropdownIcons);
   121→
   122→  // dropdown icons toggle
   123→  const dropdownToggle = (e, idx) => {
   124→    e.preventDefault();
