   133→    currentIcon.icon =
   134→      currentIcon.icon === faCaretDown ? faCaretUp : faCaretDown;
   135→
   136→    dropdownIconsVar[dropdownEl.dataset.index] = currentIcon;
   137→
   138→    setDropdownIcon(dropdownIconsVar);
   139→  };
