   213→                <div
   214→                  data-visible={mobileNav}
   215→                  className={styles.navbar__nav__close__mobile}
   216→                >
   217→                  <svg
   218→                    viewBox="0 0 20 20"
   219→                    height="30"
   220→                    width="30"
   221→                    onClick={closeMobileNav}
   222→                  >
   223→                    <path d="M15.898,4.045c-0.271-0.272-0.713-0.272-0.986,0l-4.71,4.711L5.493,4.045c-0.272-0.272-0.714-0.272-0.986,0s-0.272,0.714,0,0.986l4.709,4.711l-4.71,4.711c-0.272,0.271-0.272,0.713,0,0.986c0.136,0.136,0.314,0.203,0.492,0.203c0.179,0,0.357-0.067,0.493-0.203l4.711-4.711l4.71,4.711c0.137,0.136,0.314,0.203,0.494,0.203c0.178,0,0.355-0.067,0.492-0.203c0.273-0.273,0.273-0.715,0-0.986l-4.711-4.711l4.711-4.711C16.172,4.759,16.172,4.317,15.898,4.045z"></path>
   224→                  </svg>
   225→                </div>
   226→              </li>
   227→
   228→              {menuItems &&
