import { library } from "@fortawesome/fontawesome-svg-core";
import { faLinkedin, faWhatsapp } from "@fortawesome/free-brands-svg-icons";
import {
  faCaretDown,
  faCaretUp,
  faEnvelope,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Image from "next/image";
import Link from "next/link";
import { useEffect, useState } from "react";
import styles from "../../styles/navbar.module.css";
import DefaultButton from "../button/DefaultButton";
import ContainerLg from "../container/ContainerLg";
import NavTagline from "./NavTagline";

// register icons
library.add(faCaretDown);
library.add(faCaretUp);
library.add(faLinkedin);
library.add(faEnvelope);
library.add(faWhatsapp);

const Navbar = () => {
  // default links
  const menuItems = [
    { name: "Home", link: "/" },
    {
      name: "Information",
      link: "#",
      dropdownIndex: "1",
      childs: [
        { name: "About", link: "/about" },
        { name: "Contact", link: "/contact" },
        { name: "Problem Report", link: "/report" },
        { name: "Logos for Download", link: "/logos" },
        { name: "Fees", link: "/fees" },
        { name: "SLA", link: "/sla" },
      ],
    },
    {
      name: "Ecosystem",
      link: "#",
      dropdownIndex: "2",
      childs: [
        { name: "CXC Community Forum", link: "/community-forum" },
        { name: "Participants", link: "/participants" },
        { name: "Where to Connect", link: "/connect" },
        { name: "Statistics", link: "/stats" },
        { name: "Switch Platform", link: "/switch-platform" },
        { name: "Content Service Provider", link: "/content-service-provider" },
      ],
    },
    {
      name: "Services",
      link: "/services",
    },
    {
      name: "Technical",
      link: "#",
      dropdownIndex: "3",
      childs: [
        { name: "Allowed Traffic", link: "/allowed" },
        { name: "Route Server", link: "/rs" },
        { name: "Blackholing Service", link: "/blackhole" },
        { name: "Bogon Addresses", link: "/bogon" },
        {
          name: "Looking Glass",
          link: "https://lg.cloudxchange.id",
          target: "_blank",
        },
      ],
    },
    {
      name: "Policy",
      link: "#",
      dropdownIndex: "4",
      childs: [
        { name: "Connection Policy", link: "/connection-policy" },
        { name: "Maintenance", link: "/maintenance" },
        { name: "Privacy Policy", link: "/privacy" },
      ],
    },
  ];

  // set dropdown icons here
  const initDropdownIcons = [
    { icon: faCaretDown },
    { icon: faCaretDown },
    { icon: faCaretDown },
    { icon: faCaretDown },
    { icon: faCaretDown },
    { icon: faCaretDown },
  ];

  // mobile nav state
  const [mobileNav, setMobileNav] = useState("false");

  // toggle mobile nav
  const toggleMobileNav = (e) => {
    e.preventDefault();
    setMobileNav("true");
    document.querySelector("body").classList.toggle("noscroll");
  };

  // close nav
  const closeMobileNav = (_e) => {
    if (mobileNav == "true") {
      setMobileNav("false");
      document.querySelector("body").classList.toggle("noscroll");
    }

    let navItemsEl = document.querySelectorAll("." + styles.navbar__nav__item);
    navItemsEl.forEach((el) => {
      el.classList.remove(styles.navbar__nav__item__dropdown__active);
    });
  };

  // dropdown icons state
  const [dropdownIcons, setDropdownIcon] = useState(initDropdownIcons);

  // dropdown icons toggle
  const dropdownToggle = (e, idx) => {
    e.preventDefault();
    let dropdownEl = document.querySelector('span[data-index="' + idx + '"]');
    dropdownEl
      .closest("li")
      .classList.toggle(styles.navbar__nav__item__dropdown__active);

    let dropdownIconsVar = [...dropdownIcons];
    let currentIcon = { ...dropdownIconsVar[dropdownEl.dataset.index] };

    currentIcon.icon =
      currentIcon.icon === faCaretDown ? faCaretUp : faCaretDown;

    dropdownIconsVar[dropdownEl.dataset.index] = currentIcon;

    setDropdownIcon(dropdownIconsVar);
  };

  useEffect(() => {
    window.onresize = () => {
      const width = window.innerWidth;

      // remove mobile on larger screen
      if (width > 1023) {
        document.querySelector("body").classList.remove("noscroll");
        setMobileNav("false");

        let navItemsEl = document.querySelectorAll(
          "." + styles.navbar__nav__item
        );
        navItemsEl.forEach((el) => {
          el.classList.remove(styles.navbar__nav__item__dropdown__active);
        });
      }
    };

    window.addEventListener("scroll", () => {
      const scrollPos = window.scrollY;
      let navbarRoot = document.querySelector("." + styles.navbar__root);

      if (scrollPos >= navbarRoot.offsetHeight + 100) {
        if (!navbarRoot.classList.contains(styles.navbar__root__fixed)) {
          document.querySelector("html").style.paddingTop =
            navbarRoot.offsetHeight + "px";
          navbarRoot.classList.add(styles.navbar__root__fixed);
        }
      }

      if (scrollPos <= navbarRoot.offsetHeight + 100) {
        if (navbarRoot.classList.contains(styles.navbar__root__fixed)) {
          document.querySelector("html").style.paddingTop = null;
          navbarRoot.classList.remove(styles.navbar__root__fixed);
        }
      }
    });
  });

  return (
    <div className={styles.nav__root}>
      <NavTagline />
      <ContainerLg
        appendClass={
          "text-gray-900 text-sm border-b border-slate-100 relative z-10 " +
          styles.navbar__root
        }
      >
        <nav className={styles.navbar + " py-4 xl:py-0"}>
          <div className={styles.navbar__logo}>
            <Link
              href={"/"}
              style={{
                marginBottom: "-7px",
              }}
              passHref
            >
              <Image
                src={"/assets/img/cloudxchange.png"}
                alt={"CLOUDXCHANGE"}
                height={25}
                width={146}
              />
            </Link>
          </div>

          <div
            data-visible={mobileNav}
            className={styles.navbar__nav__container}
          >
            <ul className={styles.navbar__nav}>
              <li>
                <div
                  data-visible={mobileNav}
                  className={styles.navbar__nav__close__mobile}
                >
                  <svg
                    viewBox="0 0 20 20"
                    height="30"
                    width="30"
                    onClick={closeMobileNav}
                  >
                    <path d="M15.898,4.045c-0.271-0.272-0.713-0.272-0.986,0l-4.71,4.711L5.493,4.045c-0.272-0.272-0.714-0.272-0.986,0s-0.272,0.714,0,0.986l4.709,4.711l-4.71,4.711c-0.272,0.271-0.272,0.713,0,0.986c0.136,0.136,0.314,0.203,0.492,0.203c0.179,0,0.357-0.067,0.493-0.203l4.711-4.711l4.71,4.711c0.137,0.136,0.314,0.203,0.494,0.203c0.178,0,0.355-0.067,0.492-0.203c0.273-0.273,0.273-0.715,0-0.986l-4.711-4.711l4.711-4.711C16.172,4.759,16.172,4.317,15.898,4.045z"></path>
                  </svg>
                </div>
              </li>

              {menuItems &&
                menuItems.length > 0 &&
                menuItems.map((element, index) => {
                  if (element.dropdownIndex && element.childs) {
                    return (
                      <li key={index} className={styles.navbar__nav__item}>
                        <span
                          data-index={element.dropdownIndex}
                          onClick={(e) =>
                            dropdownToggle(e, element.dropdownIndex)
                          }
                        >
                          {element.name}{" "}
                          <FontAwesomeIcon
                            icon={dropdownIcons[element.dropdownIndex].icon}
                            className="inline lg:hidden"
                          />
                        </span>
                        <ul
                          className={
                            styles.navbar__nav__item__dropdown +
                            " text-gray-600"
                          }
                        >
                          {element.childs.map((child, index) => {
                            return (
                              <Link
                                key={index}
                                href={child.link}
                                passHref
                                legacyBehavior
                              >
                                <a onClick={closeMobileNav}>{child.name}</a>
                              </Link>
                            );
                          })}
                        </ul>
                      </li>
                    );
                  } else {
                    return (
                      <Link
                        key={index}
                        href={element.link}
                        passHref
                        legacyBehavior
                      >
                        <a
                          className={styles.navbar__nav__item}
                          onClick={closeMobileNav}
                        >
                          {element.name}
                        </a>
                      </Link>
                    );
                  }
                })}
              <li className="block xl:hidden mt-4 xl:mt-0">
                <Link href={"/join"} onClick={closeMobileNav} passHref>
                  <DefaultButton appendClass={"w-full"}>Join</DefaultButton>
                </Link>
              </li>
            </ul>
          </div>

          <div className={styles.navbar__end + " hidden xl:block"}>
            <ul className={styles.navbar__end__nav + " text-sm"}>
              <li>
                <Link href={"/join"} passHref>
                  <DefaultButton>Join</DefaultButton>
                </Link>
              </li>
            </ul>
          </div>

          <div className={styles.navbar__nav__hamburger}>
            <svg
              direction="rtl"
              viewBox="0 0 100 80"
              width="30"
              height="30"
              onClick={toggleMobileNav}
              className="rotate-180"
            >
              <rect width="70" height="8"></rect>
              <rect y="30" width="100" height="8"></rect>
              <rect y="60" width="70" height="8"></rect>
            </svg>
          </div>

          <div
            onClick={closeMobileNav}
            data-visible={mobileNav}
            className={styles.navbar__nav__backdrop}
          ></div>
        </nav>
      </ContainerLg>
    </div>
  );
};

export default Navbar;

