@keyframes show-down {
  0% {
    position: absolute;
    opacity: 0;
    background: transparent;
    -webkit-transform: translateY(-100%);
    transform: translateY(-100%);
  }

  100% {
    position: fixed;
    opacity: 1;
    -webkit-transform: translateY(0);
    transform: translateY(0);
    background: #ffffff;
    top: 0;
  }
}

.nav__root {
  position: static;
}

.navbar {
  font-family: 'Montserrat', sans-serif !important;
  background-color: transparent;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.navbar__logo {
  cursor: pointer;
  display: flex;
  align-items: center;
  flex: none;
}

.navbar__nav {
  display: flex;
  align-items: center;
  list-style: none;
}

.navbar__nav>*+* {
  margin-left: 3rem;
}

.navbar__nav__item {
  cursor: pointer;
  padding: 1rem 0;
  position: relative;
  font-weight: 500;
}

.navbar__nav__item a {
  cursor: pointer;
  padding: 0.75rem;
  font-weight: 500;
}

.navbar__nav__item:hover::after,
.navbar__nav__item:focus:after {
  width: 6px;
}

.navbar__nav__item::after {
  position: absolute;
  bottom: 0.5rem;
  left: 50%;
  content: '';
  width: 0;
  height: 6px;
  margin-left: -3px;
  background-color: rgb(220 38 38);
  transition: width 0.2s ease-in;
}

.navbar__nav__item:hover>.navbar__nav__item__dropdown,
.navbar__nav__item:focus>.navbar__nav__item__dropdown {
  opacity: 1;
  /* box-shadow: 0px 5px 15px -3px rgba(0,0,0,0.1); */
  visibility: visible;
  border-radius: 4px;
}

/* Tail arrow dropdown */
/* .navbar__nav__item:hover > .navbar__nav__item__dropdown::before, .navbar__nav__item:focus > .navbar__nav__item__dropdown::before{
      content: "";
      position: absolute;
      top: -5px;
      left: 1rem;
      border-right: 9px solid transparent;
      border-bottom: 9px solid #FFF;
      border-left: 9px solid transparent;
      display: block;
  } */

/* Dropdown spacer */
.navbar__nav__item__dropdown::before {
  content: '';
  position: absolute;
  top: -40px;
  left: 0;
  background-color: transparent;
  height: 40px;
  width: 100%;
  display: block;
}

.navbar__nav__item__dropdown {
  font-weight: normal;
  font-size: 0.9rem;
  opacity: 0;
  visibility: hidden;
  position: absolute;
  top: 4rem;
  left: -2rem;
  min-width: 15rem;
  white-space: nowrap;
  background-color: rgb(244 244 245);
  display: flex;
  flex-direction: column;
  transition: opacity 0.3s ease-in-out;
}

.navbar__nav__item__dropdown li {
  padding: 0.75rem 1rem;
}

.navbar__nav__item__dropdown li:hover {
  background-color: rgb(220 38 38);
  color: #fff;
  font-weight: 600;
}

.navbar__nav__item__dropdown li:first-child:hover {
  border-radius: 4px 4px 0 0;
}

.navbar__nav__item__dropdown li:last-child:hover {
  border-radius: 0 0 4px 4px;
}

.navbar__nav__hamburger {
  display: none;
}

.navbar__nav__backdrop {
  display: none;
}

.navbar__nav__close__mobile {
  display: none;
}

.navbar__nav__close__mobile>svg {
  fill: rgba(0, 0, 0, 0.5);
}

.navbar__end {
  padding: 0;
}

.navbar__end__nav {
  display: flex;
  gap: 1rem;
  align-items: center;
  list-style: none;
}

.navbar__end__nav li {
  cursor: pointer;
}

.navbar__root {
  display: block;
}

.navbar__root__fixed {
  -webkit-animation: show-down 0.5s linear forwards;
  animation: show-down 0.5s linear forwards;
  /* -webkit-box-shadow: 0 0 5px rgba(0, 0, 0, 0.3);
      box-shadow: 0 0 5px rgba(0, 0, 0, 0.3); */
  z-index: 10;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  /* background: #FFF; */
}

@media (max-width: 1175px) {
  .navbar__nav {
    gap: 2rem;
  }
}

@media (max-width: 1094px) {
  .navbar__nav {
    gap: 1.1rem;
  }
}

@media (max-width: 1176px) {
  .navbar__nav>*+* {
    margin-left: 0.5rem;
  }

  .navbar__nav__item__dropdown li:hover {
    background-color: #fff;
    color: rgb(220 38 38);
    font-weight: 600;
  }

  .navbar__nav__container {
    position: fixed;
    padding: 4rem 1.5rem;
    display: flex;
    width: 70vw;
    inset: 0 0 0 30%;
    background-color: #fff;
    margin: 0;
    z-index: 9999;
    transform: translateX(100%);
    transition: transform 0.4s ease-out;
    overflow-y: scroll;
    flex-direction: column;
  }

  .navbar__nav__container[data-visible='true'] {
    transform: translateX(0);
  }

  .navbar__nav {
    display: block;
    margin: 0;
    padding: 0;
    align-items: unset;
  }

  .navbar__nav__item {
    padding: 1rem 0;
    border-bottom: rgba(0, 0, 0, 0.1) solid 1px;
  }

  .navbar__nav__item>span:active {
    background-color: rgba(0, 0, 0, 0.1);
  }

  .navbar__nav__item>span {
    display: flex;
    justify-content: space-between;
  }

  .navbar__nav__item__dropdown {
    position: static;
    display: none;
    opacity: 1;
    visibility: visible;
    background: #fff;
  }

  .navbar__nav__item__dropdown__active>.navbar__nav__item__dropdown::before,
  .navbar__nav__item:focus>.navbar__nav__item__dropdown::before {
    display: none !important;
  }

  .navbar__nav__item__dropdown__active>.navbar__nav__item__dropdown {
    display: flex;
    padding: 1.5rem 1.5rem 1.5rem 0;
    box-shadow: none !important;
  }

  .navbar__nav__item::after {
    display: none;
  }

  .navbar__nav__hamburger {
    display: block;
  }

  .navbar__nav__backdrop {
    background-color: rgba(0, 0, 0, 0.7);
    top: 0;
    left: 0;
    height: 100vh;
    width: 100vw;
    position: fixed;
    z-index: 8888;
  }

  .navbar__nav__backdrop[data-visible='true'] {
    display: block !important;
  }

  .navbar__nav__close__mobile[data-visible='true'] {
    position: fixed;
    z-index: 99999;
    display: block;
    top: 1.5rem;
    right: 1rem;
  }
}

