  font-weight: 500;
}

.navbar__nav__item a {
  cursor: pointer;
  padding: 0.75rem;
  font-weight: 500;
}

.navbar__nav__item:hover::after,
.navbar__nav__item:focus:after {
  width: 6px;
}

.navbar__nav__item::after {
  position: absolute;
  bottom: 0.5rem;
  left: 50%;
  content: '';
  width: 0;
