}

.navbar__nav__item__dropdown a:first-child:hover {
  border-radius: 4px 4px 0 0;
}

.navbar__nav__item__dropdown a:last-child:hover {
  border-radius: 0 0 4px 4px;
}

.navbar__nav__hamburger {
