    padding: 0.5rem 0.75rem;
    border-radius: 4px;
    color: rgb(75 85 99);
  }

  .navbar__nav__item::after {
    display: none;
  }

  .navbar__nav__hamburger {
    display: block;
    cursor: pointer;
    padding: 0.25rem;
  }

  .navbar__nav__backdrop {
    display: none;
    background-color: rgba(0, 0, 0, 0.5);
    top: 0;
    left: 0;
    height: 100vh;
    height: 100dvh;
    width: 100vw;
    position: fixed;
    z-index: 8888;
    cursor: pointer;
    backdrop-filter: blur(2px);
