import { library } from "@fortawesome/fontawesome-svg-core";
import {
  faBolt,
  faGenderless,
  faHeart,
} from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import ContainerMd from "../container/ContainerMd";

library.add(faGenderless);
library.add(faBolt);
library.add(faHeart);

export default function HomeCard({ children, ...props }) {
  return (
    <>
      <ContainerMd appendClass={"py-16 px-8"}>
        <div className="grid grid-cols-3 gap-8">
          <div className="col-span-3 md:col-span-1 space-y-4">
            <div className="flex items-center">
              <div
                className="bg-red-600 text-white text-2xl rounded-lg justify-center flex items-center"
                style={{
                  width: "40px",
                  height: "40px",
                }}
              >
                <FontAwesomeIcon icon={faHeart} />
              </div>
              <span className="font-bold text-2xl ml-4">Community</span>
            </div>
            <div className="text-sm text-neutral-600">
              Together maintaining and growing ecosystem for better internet
              community.
            </div>
          </div>

          <div className="col-span-3 md:col-span-1 space-y-4">
            <div className="flex items-center">
              <div
                className="bg-red-600 text-white text-2xl rounded-lg justify-center flex items-center"
                style={{
                  width: "40px",
                  height: "40px",
                }}
              >
                <FontAwesomeIcon icon={faGenderless} />
              </div>
              <span className="font-bold text-2xl ml-4">Neutrality</span>
            </div>
            <div className="text-sm text-neutral-600">
              Neutral team dedicated to advancing internet connectivity.
            </div>
          </div>

          <div className="col-span-3 md:col-span-1 space-y-4">
            <div className="flex items-center">
              <div
                className="bg-red-600 text-white text-2xl rounded-lg justify-center flex items-center"
                style={{
                  width: "40px",
                  height: "40px",
                }}
              >
                <FontAwesomeIcon icon={faBolt} />
              </div>
              <span className="font-bold text-2xl ml-4">Efficiency</span>
            </div>
            <div className="text-sm text-neutral-600">
              Reduce hops, lower latency, and increase bandwidth through faster
              and direct path.
            </div>
          </div>
        </div>
      </ContainerMd>
    </>
  );
}

