// Initial dropdown icon state — defined outside component so it's stable
const initDropdownIcons = [
  { icon: faCaretDown },
  { icon: faCaretDown },
  { icon: faCaretDown },
  { icon: faCaretDown },
  { icon: faCaretDown },
  { icon: faCaretDown },
];

const Navbar = () => {
  // default links
  const menuItems = [
    { name: "Home", link: "/" },
    {
      name: "Information",
      link: "#",
      dropdownIndex: "1",
      childs: [
        { name: "About", link: "/about" },
        { name: "Contact", link: "/contact" },
