      ],
    },
    {
      name: "Ecosystem",
      link: "#",
      dropdownIndex: "2",
      childs: [
        { name: "CXC Community Forum", link: "/community-forum" },
        { name: "Participants", link: "/participants" },
        { name: "Where to Connect", link: "/connect" },
        { name: "Statistics", link: "/stats" },
        { name: "Switch Platform", link: "/switch-platform" },
        { name: "Content Service Provider", link: "/content-service-provider" },
      ],
    },
    {
      name: "Services",
      link: "/services",
    },
    {
      name: "Technical",
      link: "#",
      dropdownIndex: "3",
      childs: [
        { name: "Allowed Traffic", link: "/allowed" },
        { name: "Route Server", link: "/rs" },
        { name: "Blackholing Service", link: "/blackhole" },
