          target: "_blank",
        },
      ],
    },
    {
      name: "Policy",
      link: "#",
      dropdownIndex: "4",
      childs: [
        { name: "Connection Policy", link: "/connection-policy" },
        { name: "Maintenance", link: "/maintenance" },
        { name: "Privacy Policy", link: "/privacy" },
      ],
    },
  ];

  // mobile nav state
  const [mobileNav, setMobileNav] = useState("false");

  // dropdown icons state
  const [dropdownIcons, setDropdownIcon] = useState(initDropdownIcons);

  // Lock body scroll — saves current scroll position to avoid jump on mobile
  const lockBodyScroll = () => {
    const scrollY = window.scrollY;
    document.body.style.position = "fixed";
    document.body.style.top = `-${scrollY}px`;
    document.body.style.width = "100%";
    document.body.style.overflowY = "scroll";
  };

  // Unlock body scroll — restores saved scroll position
  const unlockBodyScroll = () => {
    const scrollY = Math.abs(parseInt(document.body.style.top || "0", 10));
    document.body.style.position = "";
    document.body.style.top = "";
    document.body.style.width = "";
    document.body.style.overflowY = "";
    window.scrollTo(0, scrollY);
  };

  // toggle mobile nav
  const toggleMobileNav = (e) => {
    e.preventDefault();
    lockBodyScroll();
    setMobileNav("true");
  };

  // close nav
  const closeMobileNav = (_e) => {
    if (mobileNav === "true") {
      unlockBodyScroll();
      setMobileNav("false");
      setDropdownIcon(initDropdownIcons);
    }

    document.querySelectorAll("." + styles.navbar__nav__item).forEach((el) => {
      el.classList.remove(styles.navbar__nav__item__dropdown__active);
    });
  };

  // dropdown icons toggle
  const dropdownToggle = (e, idx) => {
    e.preventDefault();
    let dropdownEl = document.querySelector('span[data-index="' + idx + '"]');
    dropdownEl
      .closest("li")
      .classList.toggle(styles.navbar__nav__item__dropdown__active);

    let dropdownIconsVar = [...dropdownIcons];
    let currentIcon = { ...dropdownIconsVar[dropdownEl.dataset.index] };

    currentIcon.icon =
      currentIcon.icon === faCaretDown ? faCaretUp : faCaretDown;

    dropdownIconsVar[dropdownEl.dataset.index] = currentIcon;

    setDropdownIcon(dropdownIconsVar);
  };

  useEffect(() => {
    const handleScroll = () => {
      const scrollPos = window.scrollY;
      const navbarRoot = document.querySelector("." + styles.navbar__root);
      if (!navbarRoot) return;

      if (scrollPos >= navbarRoot.offsetHeight + 100) {
        if (!navbarRoot.classList.contains(styles.navbar__root__fixed)) {
          document.querySelector("html").style.paddingTop =
            navbarRoot.offsetHeight + "px";
          navbarRoot.classList.add(styles.navbar__root__fixed);
        }
      }

      if (scrollPos <= navbarRoot.offsetHeight + 100) {
        if (navbarRoot.classList.contains(styles.navbar__root__fixed)) {
          document.querySelector("html").style.paddingTop = null;
          navbarRoot.classList.remove(styles.navbar__root__fixed);
        }
      }
    };

    const handleResize = () => {
      if (window.innerWidth > 1176) {
        // Restore body scroll if mobile menu was open
        unlockBodyScroll();
        setMobileNav("false");
        setDropdownIcon(initDropdownIcons);
        document.querySelectorAll("." + styles.navbar__nav__item).forEach((el) => {
          el.classList.remove(styles.navbar__nav__item__dropdown__active);
        });
      }
    };

    window.addEventListener("scroll", handleScroll, { passive: true });
    window.addEventListener("resize", handleResize);

    return () => {
      window.removeEventListener("scroll", handleScroll);
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  return (
    <div className={styles.nav__root}>
      <NavTagline />
      <ContainerLg
        appendClass={
          "text-gray-900 text-sm border-b border-slate-100 relative z-10 " +
          styles.navbar__root
        }
      >
        <nav className={styles.navbar + " py-4 xl:py-0"}>
          <div className={styles.navbar__logo}>
            <Link
              href={"/"}
              style={{
                marginBottom: "-7px",
              }}
              passHref
            >
              <Image
                src={"/assets/img/cloudxchange.png"}
                alt={"CLOUDXCHANGE"}
                height={25}
                width={146}
              />
            </Link>
          </div>

          <div
            data-visible={mobileNav}
            className={styles.navbar__nav__container}
          >
            <ul className={styles.navbar__nav}>
              <li>
                <div
                  data-visible={mobileNav}
                  className={styles.navbar__nav__close__mobile}
                  onClick={closeMobileNav}
                  aria-label="Close menu"
                >
                  <svg
                    viewBox="0 0 20 20"
                    height="20"
                    width="20"
                  >
                    <path d="M15.898,4.045c-0.271-0.272-0.713-0.272-0.986,0l-4.71,4.711L5.493,4.045c-0.272-0.272-0.714-0.272-0.986,0s-0.272,0.714,0,0.986l4.709,4.711l-4.71,4.711c-0.272,0.271-0.272,0.713,0,0.986c0.136,0.136,0.314,0.203,0.492,0.203c0.179,0,0.357-0.067,0.493-0.203l4.711-4.711l4.71,4.711c0.137,0.136,0.314,0.203,0.494,0.203c0.178,0,0.355-0.067,0.492-0.203c0.273-0.273,0.273-0.715,0-0.986l-4.711-4.711l4.711-4.711C16.172,4.759,16.172,4.317,15.898,4.045z"></path>
                  </svg>
                </div>
              </li>

              {menuItems &&
                menuItems.length > 0 &&
                menuItems.map((element, index) => {
                  if (element.dropdownIndex && element.childs) {
                    return (
                      <li key={index} className={styles.navbar__nav__item}>
                        <span
                          data-index={element.dropdownIndex}
                          onClick={(e) =>
                            dropdownToggle(e, element.dropdownIndex)
