export async function getServerSideProps(ctx) {
  try {
    const partRequest = await fetch("http://api.cloudxchange.id/res.json");
    const partData = await partRequest.json();

    return {
      props: {
        memberList: partData.member_list,
      },
    };
  } catch (error) {
    console.log(error);
    return {
      props: {
        memberList: [],
      },
    };
